package Curs4;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// num++ -> post increment  adica num = num+1
		// ++num -> pre increment num = num +1
		
		int num = 10;
		System.out.println("Valloare lui num inainte de increment: " + num);
		System.out.println("Valoare lui num in post increment : " + num++);
		System.out.println("Valoare lui num DUPA post increment : " + num);
		System.out.println("Valoare lui num in pre increment : " + ++num);

		
	}

}
