package Curs4;

public class WhileLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 0;
		
		while (i<10) {
			i++;
			System.out.println(i);
		}
		
		int j = 0;
		String[] array = {"one", "two", "three", "four"};
		
		while (j<array.length) {
			System.out.println(array[j]);
			j++;

		}

	}

}
